from pyecharts import Bar, Pie, Grid
import pandas as pd
from pyecharts_javascripthon.dom import window
home_icon = "image://data:image/png;base64,"


sheet0 = pd.read_excel("./data_resources/6.1去重全事业部各个支出类型费用占比.xls")
group0 = sheet0["单据类型"][1:].values.tolist()
days0 = sheet0["金额（万元）"][1:].values.tolist()
bar0 = Bar('全事业部中各种支付类型的金额', title_pos="0%")
bar0.add('支出类型',group0,days0,xaxis_rotate =30, yaxis_name = '金额(万元)',yaxis_name_pos = 'middle',yaxis_name_gap = 40,legend_pos="40%",is_label_show = True, is_stack=True)
bar0.render("./all_charts/"+"6.1全事业部各个支出类型费用占比柱状图" + ".html")


